    JOIN delivery_items di ON di.delivery_id = d.id
    WHERE (p_branch_id IS NULL OR d.branch_id = p_branch_id)
    GROUP BY d.transaction_id, di.product_id
  ),
  transaction_items AS (
    SELECT 
      t.id as txn_id,
