--
-- Name: process_delivery_atomic(uuid, timestamp without time zone, uuid, uuid, text); Type: FUNCTION; Schema: public; Owner: aquavit
--

CREATE FUNCTION public.process_delivery_atomic(p_transaction_id uuid, p_delivery_date timestamp without time zone DEFAULT now(), p_branch_id uuid DEFAULT NULL::uuid, p_user_id uuid DEFAULT NULL::uuid, p_user_name text DEFAULT 'System'::text) RETURNS TABLE(success boolean, delivery_id uuid, total_hpp numeric, journal_id uuid, error_message text)
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_transaction RECORD;
  v_item RECORD;
  v_total_hpp NUMERIC := 0;
  v_journal_id UUID;
  v_fifo_result RECORD;
  v_delivery_id UUID;
  v_hpp_details TEXT := '';
BEGIN
  -- Get transaction details
  SELECT
    t.id,
    t.ref,
    t.customer_name,
    t.total,
    t.branch_id,
    t.is_delivered,
    t.delivery_id as existing_delivery_id
  INTO v_transaction
  FROM transactions t
  WHERE t.id = p_transaction_id;

  IF v_transaction.id IS NULL THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID, 'Transaction not found'::TEXT;
    RETURN;
  END IF;

  IF v_transaction.is_delivered THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID, 'Transaction already delivered'::TEXT;
    RETURN;
  END IF;

  -- Use transaction branch if not specified
  IF p_branch_id IS NULL THEN
    p_branch_id := v_transaction.branch_id;
  END IF;

  -- Create delivery record
  INSERT INTO deliveries (
    transaction_id,
    delivery_date,
    status,
    branch_id,
    created_by
  ) VALUES (
    p_transaction_id,
    p_delivery_date,
    'completed',
    p_branch_id,
    p_user_id
  )
  RETURNING id INTO v_delivery_id;

  -- Process each item - consume stock via FIFO
  FOR v_item IN
    SELECT
      ti.product_id,
      ti.quantity,
      p.name as product_name
    FROM transaction_items ti
    JOIN products p ON p.id = ti.product_id
    WHERE ti.transaction_id = p_transaction_id
  LOOP
    -- Consume inventory via FIFO
    SELECT * INTO v_fifo_result
    FROM consume_inventory_fifo(
      v_item.product_id,
      p_branch_id,
      v_item.quantity,
      p_transaction_id::TEXT,
      NULL  -- material_id null for products
    );

    IF v_fifo_result.total_hpp > 0 THEN
      v_total_hpp := v_total_hpp + v_fifo_result.total_hpp;
      v_hpp_details := v_hpp_details || format('%s x%s (Rp%s), ',
        v_item.product_name, v_item.quantity, ROUND(v_fifo_result.total_hpp));
    END IF;
  END LOOP;

  -- Update transaction as delivered
  UPDATE transactions
  SET
    is_delivered = TRUE,
    delivery_id = v_delivery_id,
    delivered_at = p_delivery_date,
    hpp = v_total_hpp,
    updated_at = NOW()
  WHERE id = p_transaction_id;

  -- Create HPP journal if total > 0
  IF v_total_hpp > 0 AND p_branch_id IS NOT NULL THEN
    DECLARE
      v_hpp_account_id UUID;
      v_persediaan_id UUID;
      v_entry_number TEXT;
    BEGIN
      -- Get accounts: HPP (5200) and Persediaan Barang Dagang (1310)
      SELECT id INTO v_hpp_account_id
      FROM accounts
      WHERE code = '5200' AND branch_id = p_branch_id AND is_active = TRUE
      LIMIT 1;

      SELECT id INTO v_persediaan_id
      FROM accounts
      WHERE code = '1310' AND branch_id = p_branch_id AND is_active = TRUE
      LIMIT 1;

      IF v_hpp_account_id IS NOT NULL AND v_persediaan_id IS NOT NULL THEN
        -- Generate entry number
        v_entry_number := 'JE-' || TO_CHAR(NOW(), 'YYYYMMDD') || '-' ||
          LPAD((SELECT COUNT(*) + 1 FROM journal_entries WHERE branch_id = p_branch_id AND DATE(created_at) = CURRENT_DATE)::TEXT, 4, '0');

        -- Create journal header
        INSERT INTO journal_entries (
          entry_number,
          entry_date,
          description,
          reference_type,
          reference_id,
          branch_id,
          status,
          total_debit,
          total_credit
        ) VALUES (
          v_entry_number,
          p_delivery_date,
          format('HPP Pengiriman %s: %s', v_transaction.ref, v_transaction.customer_name),
          'transaction',
          v_delivery_id,
          p_branch_id,
          'posted',
          v_total_hpp,
          v_total_hpp
        )
        RETURNING id INTO v_journal_id;

        -- Create journal lines
        -- Dr. HPP (5200)
        INSERT INTO journal_lines (
          journal_id,
          account_id,
          description,
          debit_amount,
          credit_amount
        ) VALUES (
          v_journal_id,
          v_hpp_account_id,
          format('HPP: %s', RTRIM(v_hpp_details, ', ')),
          v_total_hpp,
          0
        );

        -- Cr. Persediaan Barang Dagang (1310)
        INSERT INTO journal_lines (
          journal_id,
          account_id,
          description,
          debit_amount,
          credit_amount
        ) VALUES (
          v_journal_id,
          v_persediaan_id,
          format('Stock keluar: %s', v_transaction.ref),
          0,
          v_total_hpp
        );
      END IF;
    END;
  END IF;

  RETURN QUERY SELECT
    TRUE,
    v_delivery_id,
    v_total_hpp,
    v_journal_id,
    NULL::TEXT;

EXCEPTION WHEN OTHERS THEN
