--
-- Name: receive_payment_atomic(text, uuid, numeric, text, date, text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.receive_payment_atomic(p_receivable_id text, p_branch_id uuid, p_amount numeric, p_payment_method text DEFAULT 'cash'::text, p_payment_date date DEFAULT CURRENT_DATE, p_notes text DEFAULT NULL::text) RETURNS TABLE(success boolean, payment_id uuid, remaining_amount numeric, journal_id uuid, error_message text)
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_payment_id UUID;
  v_receivable RECORD;
  v_remaining NUMERIC;
  v_journal_id UUID;
  v_entry_number TEXT;
  v_kas_account_id TEXT;      -- accounts.id is TEXT
  v_piutang_account_id TEXT;  -- accounts.id is TEXT
BEGIN
  -- ==================== VALIDASI ====================

  IF p_branch_id IS NULL THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID,
      'Branch ID is REQUIRED - tidak boleh lintas cabang!'::TEXT;
    RETURN;
  END IF;

  IF p_receivable_id IS NULL THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID,
      'Receivable ID is required'::TEXT;
    RETURN;
  END IF;

  IF p_amount <= 0 THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID,
      'Amount must be positive'::TEXT;
    RETURN;
  END IF;

  -- Get transaction info (acting as receivable)
  SELECT
    t.id,
    t.customer_id,
    t.total,
    COALESCE(t.paid_amount, 0) as paid_amount,
    COALESCE(t.total - COALESCE(t.paid_amount, 0), 0) as remaining_amount,
    t.payment_status as status,
    c.name as customer_name
  INTO v_receivable
  FROM transactions t
  LEFT JOIN customers c ON c.id = t.customer_id
  WHERE t.id = p_receivable_id::TEXT AND t.branch_id = p_branch_id; -- Cast UUID param to TEXT for transactions.id

  IF v_receivable.id IS NULL THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID,
      'Transaction not found in this branch'::TEXT;
    RETURN;
  END IF;

  IF v_receivable.status = 'paid' THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID,
      'Transaction already fully paid'::TEXT;
    RETURN;
  END IF;

  -- Calculate new remaining
  v_remaining := GREATEST(0, v_receivable.remaining_amount - p_amount);

  -- ==================== CREATE PAYMENT RECORD ====================
  -- Using transaction_payments table
  
  INSERT INTO transaction_payments (
    transaction_id,
    branch_id,
    amount,
    payment_method,
    payment_date,
    notes,
    created_at
  ) VALUES (
    p_receivable_id::TEXT,
    p_branch_id,
    p_amount,
    p_payment_method,
    p_payment_date,
    COALESCE(p_notes, format('Payment from %s', COALESCE(v_receivable.customer_name, 'Customer'))),
    NOW()
  )
  RETURNING id INTO v_payment_id;

  -- ==================== UPDATE TRANSACTION ====================

  UPDATE transactions
  SET
    paid_amount = COALESCE(paid_amount, 0) + p_amount,
    payment_status = CASE WHEN v_remaining <= 0 THEN 'Lunas' ELSE 'Partial' END,
    updated_at = NOW()
  WHERE id = p_receivable_id::TEXT;

  -- ==================== CREATE JOURNAL ENTRY ====================

  -- Get account IDs based on payment method
  IF p_payment_method = 'transfer' THEN
    SELECT id INTO v_kas_account_id
    FROM accounts
    WHERE code = '1120' AND branch_id = p_branch_id AND is_active = TRUE
    LIMIT 1;
  ELSE
    SELECT id INTO v_kas_account_id
    FROM accounts
    WHERE code = '1110' AND branch_id = p_branch_id AND is_active = TRUE
    LIMIT 1;
  END IF;

  SELECT id INTO v_piutang_account_id
  FROM accounts
  WHERE code = '1210' AND branch_id = p_branch_id AND is_active = TRUE
  LIMIT 1;

  IF v_kas_account_id IS NOT NULL AND v_piutang_account_id IS NOT NULL THEN
    v_entry_number := 'JE-' || TO_CHAR(p_payment_date, 'YYYYMMDD') || '-' ||
      LPAD((SELECT COUNT(*) + 1 FROM journal_entries
            WHERE branch_id = p_branch_id
            AND DATE(created_at) = p_payment_date)::TEXT, 4, '0');

    INSERT INTO journal_entries (
      entry_number,
      entry_date,
      description,
      reference_type,
      reference_id,
      branch_id,
      status,
      total_debit,
      total_credit
    ) VALUES (
      v_entry_number,
      p_payment_date,
      format('Terima pembayaran piutang: %s', COALESCE(v_receivable.customer_name, 'Customer')),
      'receivable_payment',
      v_payment_id::TEXT,
      p_branch_id,
      'draft',
      p_amount,
      p_amount
    )
    RETURNING id INTO v_journal_id;

    -- Dr. Kas/Bank
    INSERT INTO journal_entry_lines (
      journal_entry_id,
      line_number,
      account_id,
      description,
      debit_amount,
      credit_amount
    ) VALUES (
      v_journal_id,
      1,
      v_kas_account_id,
      format('Terima dari %s', COALESCE(v_receivable.customer_name, 'Customer')),
      p_amount,
      0
    );

    -- Cr. Piutang Usaha
    INSERT INTO journal_entry_lines (
      journal_entry_id,
      line_number,
      account_id,
      description,
      debit_amount,
      credit_amount
    ) VALUES (
      v_journal_id,
      2,
      v_piutang_account_id,
      format('Pelunasan piutang: %s', COALESCE(v_receivable.customer_name, 'Customer')),
      0,
      p_amount
    );

    UPDATE journal_entries SET status = 'posted' WHERE id = v_journal_id;
  END IF;

  RETURN QUERY SELECT
    TRUE,
    v_payment_id,
    v_remaining,
    v_journal_id,
    NULL::TEXT;

EXCEPTION WHEN OTHERS THEN
  RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, NULL::UUID, SQLERRM::TEXT;
END;
$$;


ALTER FUNCTION public.receive_payment_atomic(p_receivable_id text, p_branch_id uuid, p_amount numeric, p_payment_method text, p_payment_date date, p_notes text) OWNER TO postgres;

--
-- Name: receive_payment_atomic(uuid, numeric, text, timestamp without time zone, uuid, text, uuid, uuid, text); Type: FUNCTION; Schema: public; Owner: aquavit
--

CREATE FUNCTION public.receive_payment_atomic(p_receivable_id uuid, p_amount numeric, p_payment_method text DEFAULT 'cash'::text, p_payment_date timestamp without time zone DEFAULT now(), p_payment_account_id uuid DEFAULT NULL::uuid, p_notes text DEFAULT NULL::text, p_branch_id uuid DEFAULT NULL::uuid, p_user_id uuid DEFAULT NULL::uuid, p_user_name text DEFAULT 'System'::text) RETURNS TABLE(success boolean, payment_id uuid, remaining_amount numeric, is_fully_paid boolean, journal_id uuid, error_message text)
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_receivable RECORD;
  v_payment_id UUID;
  v_journal_id UUID;
  v_remaining NUMERIC;
  v_is_fully_paid BOOLEAN;
  v_kas_account_id UUID;
  v_piutang_account_id UUID;
BEGIN
  -- Get receivable details
  SELECT
    r.id,
    r.amount,
    r.amount_paid,
    r.status,
    r.customer_id,
    r.transaction_id,
    r.branch_id,
    c.name as customer_name
  INTO v_receivable
  FROM receivables r
  LEFT JOIN customers c ON c.id = r.customer_id
  WHERE r.id = p_receivable_id;

  IF v_receivable.id IS NULL THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, FALSE, NULL::UUID, 'Receivable not found'::TEXT;
    RETURN;
  END IF;

  IF v_receivable.status = 'paid' THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, TRUE, NULL::UUID, 'Receivable already fully paid'::TEXT;
    RETURN;
  END IF;

  IF v_receivable.status = 'cancelled' THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, FALSE, NULL::UUID, 'Receivable is cancelled'::TEXT;
    RETURN;
  END IF;

  -- Use receivable branch if not specified
  IF p_branch_id IS NULL THEN
    p_branch_id := v_receivable.branch_id;
  END IF;

  -- Calculate remaining
  v_remaining := v_receivable.amount - COALESCE(v_receivable.amount_paid, 0) - p_amount;
  v_is_fully_paid := v_remaining <= 0;

  -- Validate payment amount
  IF p_amount <= 0 THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, FALSE, NULL::UUID, 'Payment amount must be positive'::TEXT;
    RETURN;
  END IF;

  IF p_amount > (v_receivable.amount - COALESCE(v_receivable.amount_paid, 0)) THEN
    RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, FALSE, NULL::UUID, 'Payment exceeds remaining amount'::TEXT;
    RETURN;
  END IF;

  -- Record payment
  INSERT INTO receivable_payments (
    receivable_id,
    amount,
    payment_method,
    payment_date,
    payment_account_id,
    notes,
    created_by
  ) VALUES (
    p_receivable_id,
    p_amount,
    p_payment_method,
    p_payment_date,
    p_payment_account_id,
    p_notes,
    p_user_id
  )
  RETURNING id INTO v_payment_id;

  -- Update receivable
  UPDATE receivables
  SET
    amount_paid = COALESCE(amount_paid, 0) + p_amount,
    status = CASE WHEN v_is_fully_paid THEN 'paid' ELSE 'partial' END,
    updated_at = NOW()
  WHERE id = p_receivable_id;

  -- Create journal entry
  IF p_branch_id IS NOT NULL THEN
    -- Get or use specified payment account
    IF p_payment_account_id IS NOT NULL THEN
      v_kas_account_id := p_payment_account_id;
    ELSE
      SELECT id INTO v_kas_account_id
      FROM accounts
      WHERE code = '1110' AND branch_id = p_branch_id AND is_active = TRUE
      LIMIT 1;
    END IF;

    SELECT id INTO v_piutang_account_id
    FROM accounts
    WHERE code = '1210' AND branch_id = p_branch_id AND is_active = TRUE
    LIMIT 1;

    IF v_kas_account_id IS NOT NULL AND v_piutang_account_id IS NOT NULL THEN
      DECLARE
        v_entry_number TEXT;
      BEGIN
        v_entry_number := 'JE-' || TO_CHAR(NOW(), 'YYYYMMDD') || '-' ||
          LPAD((SELECT COUNT(*) + 1 FROM journal_entries WHERE branch_id = p_branch_id AND DATE(created_at) = CURRENT_DATE)::TEXT, 4, '0');

        INSERT INTO journal_entries (
          entry_number,
          entry_date,
          description,
          reference_type,
          reference_id,
          branch_id,
          status,
          total_debit,
          total_credit
        ) VALUES (
          v_entry_number,
          p_payment_date,
          format('Terima pembayaran piutang: %s', COALESCE(v_receivable.customer_name, 'Unknown')),
          'receivable',
          v_payment_id,
          p_branch_id,
          'posted',
          p_amount,
          p_amount
        )
        RETURNING id INTO v_journal_id;

        -- Dr. Kas/Bank
        INSERT INTO journal_lines (
          journal_id,
          account_id,
          description,
          debit_amount,
          credit_amount
        ) VALUES (
          v_journal_id,
          v_kas_account_id,
          format('Terima dari %s', COALESCE(v_receivable.customer_name, 'Unknown')),
          p_amount,
          0
        );

        -- Cr. Piutang Usaha
        INSERT INTO journal_lines (
          journal_id,
          account_id,
          description,
          debit_amount,
          credit_amount
        ) VALUES (
          v_journal_id,
          v_piutang_account_id,
          format('Pelunasan piutang: %s', COALESCE(v_receivable.customer_name, 'Unknown')),
          0,
          p_amount
        );
      END;
    END IF;
  END IF;

  RETURN QUERY SELECT
    TRUE,
    v_payment_id,
    GREATEST(0, v_remaining),
    v_is_fully_paid,
    v_journal_id,
    NULL::TEXT;

EXCEPTION WHEN OTHERS THEN
  RETURN QUERY SELECT FALSE, NULL::UUID, 0::NUMERIC, FALSE, NULL::UUID, SQLERRM::TEXT;
END;
$$;


