-- Name: restore_material_fifo(uuid, numeric, text, text, uuid, uuid, text); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.restore_material_fifo(p_material_id uuid, p_quantity numeric, p_reference_id text, p_reference_type text, p_branch_id uuid DEFAULT NULL::uuid, p_user_id uuid DEFAULT NULL::uuid, p_user_name text DEFAULT NULL::text) RETURNS TABLE(success boolean, total_restored numeric, error_message text)
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_current_stock NUMERIC;
  v_new_stock NUMERIC;
  v_material_name TEXT;
  v_material_type TEXT;
BEGIN
  IF p_material_id IS NULL OR p_quantity <= 0 THEN
    RETURN QUERY SELECT FALSE, 0::NUMERIC, 'Invalid parameters'::TEXT;
    RETURN;
  END IF;

  -- Get material info
  SELECT name, stock, type INTO v_material_name, v_current_stock, v_material_type
  FROM materials WHERE id = p_material_id;

  IF v_material_name IS NULL THEN
    RETURN QUERY SELECT FALSE, 0::NUMERIC, 'Material not found'::TEXT;
    RETURN;
  END IF;

  -- Calculate new stock (reverse of consume)
  IF v_material_type = 'Stock' THEN
    v_new_stock := v_current_stock + p_quantity;
  ELSE
    v_new_stock := GREATEST(0, v_current_stock - p_quantity);
  END IF;

  -- Update material stock
  UPDATE materials
  SET stock = v_new_stock, updated_at = NOW()
  WHERE id = p_material_id;

  -- Log to material_stock_movements
  INSERT INTO material_stock_movements (
    material_id,
    material_name,
    type,
    reason,
    quantity,
    previous_stock,
    new_stock,
    reference_id,
    reference_type,
    user_id,
    user_name,
    notes,
    branch_id
  ) VALUES (
    p_material_id,
    v_material_name,
    'IN',
    'PRODUCTION_DELETE_RESTORE',
    p_quantity,
    v_current_stock,
    v_new_stock,
    p_reference_id,
    p_reference_type,
    p_user_id,
    COALESCE(p_user_name, 'System'),
    format('FIFO restore for cancelled %s', p_reference_id),
    p_branch_id
  );

  RETURN QUERY SELECT TRUE, p_quantity, NULL::TEXT;
END;
$$;


