--
-- Name: void_transaction_atomic(text, uuid, text, uuid); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.void_transaction_atomic(p_transaction_id text, p_branch_id uuid, p_reason text DEFAULT 'Cancelled'::text, p_user_id uuid DEFAULT NULL::uuid) RETURNS TABLE(success boolean, items_restored integer, journals_voided integer, commissions_deleted integer, deliveries_deleted integer, error_message text)
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
  v_transaction RECORD;
  v_items_restored INTEGER := 0;
  v_journals_voided INTEGER := 0;
  v_commissions_deleted INTEGER := 0;
  v_deliveries_deleted INTEGER := 0;
  v_item RECORD;
  v_batch RECORD;
  v_restore_qty NUMERIC;
BEGIN
  -- ==================== VALIDASI ====================

  IF p_branch_id IS NULL THEN
    RETURN QUERY SELECT FALSE, 0, 0, 0, 0, 'Branch ID is REQUIRED!'::TEXT;
    RETURN;
  END IF;

  -- Get transaction with row lock
  SELECT * INTO v_transaction
  FROM transactions
  WHERE id = p_transaction_id AND branch_id = p_branch_id
  FOR UPDATE;

  IF v_transaction.id IS NULL THEN
    RETURN QUERY SELECT FALSE, 0, 0, 0, 0, 'Transaction not found'::TEXT;
    RETURN;
  END IF;

  -- ==================== RESTORE INVENTORY ====================

  -- IF Office Sale (immediate consume) OR already delivered (consume via delivery)
  IF v_transaction.is_office_sale OR v_transaction.delivery_status = 'Delivered' THEN
    -- Parse items from JSONB
    FOR v_item IN 
      SELECT 
        (elem->>'productId')::UUID as product_id,
        (elem->>'quantity')::NUMERIC as quantity
      FROM jsonb_array_elements(v_transaction.items) as elem
      WHERE (elem->>'productId') IS NOT NULL
    LOOP
      v_restore_qty := v_item.quantity;

      -- Restore to batches in LIFO order (newest first)
      FOR v_batch IN
        SELECT id, remaining_quantity, initial_quantity
        FROM inventory_batches
        WHERE product_id = v_item.product_id
          AND branch_id = p_branch_id
          AND remaining_quantity < initial_quantity
        ORDER BY batch_date DESC, created_at DESC
        FOR UPDATE
      LOOP
        EXIT WHEN v_restore_qty <= 0;

        DECLARE
          v_can_restore NUMERIC;
        BEGIN
          v_can_restore := LEAST(v_restore_qty, v_batch.initial_quantity - v_batch.remaining_quantity);

          UPDATE inventory_batches
          SET
            remaining_quantity = remaining_quantity + v_can_restore,
            updated_at = NOW()
          WHERE id = v_batch.id;

          v_restore_qty := v_restore_qty - v_can_restore;
        END;
      END LOOP;

      -- If still have qty to restore, create new batch
      IF v_restore_qty > 0 THEN
        INSERT INTO inventory_batches (
          product_id,
          branch_id,
          initial_quantity,
          remaining_quantity,
          unit_cost,
          batch_date,
          notes
        ) VALUES (
          v_item.product_id,
          p_branch_id,
          v_restore_qty,
          v_restore_qty,
          0,
          NOW(),
          format('Restored from void: %s', v_transaction.id)
        );
      END IF;
      
      v_items_restored := v_items_restored + 1;
    END LOOP;
  END IF;

  -- ==================== VOID JOURNALS ====================

  UPDATE journal_entries
  SET
    is_voided = TRUE,
    voided_at = NOW(),
    voided_reason = p_reason,
    status = 'voided',
    updated_at = NOW()
  WHERE reference_type = 'transaction'
    AND reference_id = p_transaction_id
    AND branch_id = p_branch_id
    AND is_voided = FALSE;

  GET DIAGNOSTICS v_journals_voided = ROW_COUNT;

  -- Void related delivery journals
  UPDATE journal_entries
  SET
    is_voided = TRUE,
    voided_at = NOW(),
    voided_reason = 'Transaction voided: ' || p_reason,
    status = 'voided',
    updated_at = NOW()
  WHERE reference_type = 'delivery'
    AND reference_id IN (SELECT id::TEXT FROM deliveries WHERE transaction_id = p_transaction_id)
    AND branch_id = p_branch_id
    AND is_voided = FALSE;

  -- ==================== DELETE COMMISSIONS ====================

  DELETE FROM commission_entries
  WHERE transaction_id = p_transaction_id AND branch_id = p_branch_id;

  GET DIAGNOSTICS v_commissions_deleted = ROW_COUNT;

  -- ==================== DELETE DELIVERIES ====================

  DELETE FROM delivery_items
  WHERE delivery_id IN (SELECT id FROM deliveries WHERE transaction_id = p_transaction_id);

  DELETE FROM deliveries
  WHERE transaction_id = p_transaction_id AND branch_id = p_branch_id;

  GET DIAGNOSTICS v_deliveries_deleted = ROW_COUNT;

  -- ==================== DELETE STOCK MOVEMENTS ====================

  DELETE FROM product_stock_movements
  WHERE reference_id = p_transaction_id AND reference_type IN ('transaction', 'delivery', 'fifo_consume');

  -- ==================== CANCEL RECEIVABLES ====================
  
  UPDATE receivables
  SET status = 'cancelled', updated_at = NOW()
  WHERE transaction_id = p_transaction_id AND branch_id = p_branch_id;

  -- ==================== DELETE TRANSACTION ====================

  -- Hard delete the transaction (not soft delete)
  DELETE FROM transactions
  WHERE id = p_transaction_id AND branch_id = p_branch_id;

  -- ==================== SUCCESS ====================

  RETURN QUERY SELECT
    TRUE,
    v_items_restored,
    v_journals_voided,
    v_commissions_deleted,
    v_deliveries_deleted,
    NULL::TEXT;

EXCEPTION WHEN OTHERS THEN
  RETURN QUERY SELECT FALSE, 0, 0, 0, 0, SQLERRM::TEXT;
END;

$$;


ALTER FUNCTION public.void_transaction_atomic(p_transaction_id text, p_branch_id uuid, p_reason text, p_user_id uuid) OWNER TO postgres;

--
-- Name: void_transaction_atomic(uuid, text, uuid, text); Type: FUNCTION; Schema: public; Owner: aquavit
--

CREATE FUNCTION public.void_transaction_atomic(p_transaction_id uuid, p_void_reason text DEFAULT 'Void by user'::text, p_user_id uuid DEFAULT NULL::uuid, p_user_name text DEFAULT 'System'::text) RETURNS TABLE(success boolean, stock_restored boolean, journal_voided boolean, receivable_cancelled boolean, error_message text)
    LANGUAGE plpgsql
    AS $$
DECLARE
  v_transaction RECORD;
  v_item RECORD;
  v_batch RECORD;
  v_stock_restored BOOLEAN := FALSE;
  v_journal_voided BOOLEAN := FALSE;
  v_receivable_cancelled BOOLEAN := FALSE;
  v_restore_qty NUMERIC;
BEGIN
  -- Get transaction details
  SELECT
    t.id,
    t.ref,
    t.is_delivered,
    t.is_voided,
    t.delivery_id,
    t.hpp,
    t.branch_id,
    t.payment_status,
    t.customer_id
  INTO v_transaction
  FROM transactions t
  WHERE t.id = p_transaction_id;

  IF v_transaction.id IS NULL THEN
    RETURN QUERY SELECT FALSE, FALSE, FALSE, FALSE, 'Transaction not found'::TEXT;
    RETURN;
  END IF;

  IF v_transaction.is_voided THEN
    RETURN QUERY SELECT FALSE, FALSE, FALSE, FALSE, 'Transaction already voided'::TEXT;
    RETURN;
  END IF;

  -- If delivered, restore stock using LIFO (newest batch first)
  IF v_transaction.is_delivered THEN
    FOR v_item IN
      SELECT
        ti.product_id,
        ti.quantity,
        p.name as product_name
      FROM transaction_items ti
      JOIN products p ON p.id = ti.product_id
      WHERE ti.transaction_id = p_transaction_id
    LOOP
      v_restore_qty := v_item.quantity;

      -- Restore to batches in LIFO order (newest first)
      FOR v_batch IN
        SELECT id, remaining_quantity, initial_quantity
        FROM inventory_batches
        WHERE product_id = v_item.product_id
          AND (branch_id = v_transaction.branch_id OR branch_id IS NULL)
          AND remaining_quantity < initial_quantity
        ORDER BY batch_date DESC, created_at DESC
      LOOP
        EXIT WHEN v_restore_qty <= 0;

        DECLARE
          v_can_restore NUMERIC;
        BEGIN
          v_can_restore := LEAST(v_restore_qty, v_batch.initial_quantity - v_batch.remaining_quantity);

          UPDATE inventory_batches
          SET
            remaining_quantity = remaining_quantity + v_can_restore,
            updated_at = NOW()
          WHERE id = v_batch.id;

          v_restore_qty := v_restore_qty - v_can_restore;
        END;
      END LOOP;

      -- If still have qty to restore, create new batch
      IF v_restore_qty > 0 THEN
        INSERT INTO inventory_batches (
          product_id,
          branch_id,
          initial_quantity,
          remaining_quantity,
          unit_cost,
          batch_date,
          notes
        ) VALUES (
          v_item.product_id,
          v_transaction.branch_id,
          v_restore_qty,
          v_restore_qty,
          0, -- Unknown cost for restored stock
          NOW(),
          format('Restored from void: %s', v_transaction.ref)
        );
      END IF;
    END LOOP;

    v_stock_restored := TRUE;
  END IF;

  -- Void related journal entries
  UPDATE journal_entries
  SET
    is_voided = TRUE,
    voided_at = NOW(),
    voided_by = p_user_id,
    void_reason = p_void_reason,
    status = 'voided'
  WHERE reference_id = p_transaction_id::TEXT
    OR reference_id = v_transaction.delivery_id::TEXT;

  IF FOUND THEN
    v_journal_voided := TRUE;
  END IF;

  -- Cancel receivable if exists
  IF v_transaction.payment_status = 'credit' OR v_transaction.payment_status = 'partial' THEN
    UPDATE receivables
    SET
      status = 'cancelled',
      updated_at = NOW()
    WHERE transaction_id = p_transaction_id;

    IF FOUND THEN
      v_receivable_cancelled := TRUE;
    END IF;
  END IF;

  -- Mark transaction as voided
  UPDATE transactions
  SET
    is_voided = TRUE,
    voided_at = NOW(),
    voided_by = p_user_id,
    void_reason = p_void_reason,
    updated_at = NOW()
  WHERE id = p_transaction_id;

  RETURN QUERY SELECT
    TRUE,
    v_stock_restored,
    v_journal_voided,
    v_receivable_cancelled,
    NULL::TEXT;

EXCEPTION WHEN OTHERS THEN
  RETURN QUERY SELECT FALSE, FALSE, FALSE, FALSE, SQLERRM::TEXT;
END;
$$;


