    pr.net_salary,
    pr.status,
    pr.paid_date AS payment_date,
    pr.notes,
    pr.branch_id,
    pr.created_by,
    pr.created_at,
    pr.updated_at
   FROM (public.payroll_records pr
     LEFT JOIN public.profiles p ON ((pr.employee_id = p.id)));


